local a=game.Players.LocalPlayer;local b=a.Character or a.CharacterAdded:Wait()local c=b:WaitForChild("HumanoidRootPart")local d=false;local e=50;local f=game:GetService("UserInputService")local g=game:GetService("RunService")local h={forward=0,back=0,left=0,right=0,up=0,down=0}function fly()local i=Instance.new("BodyVelocity")i.MaxForce=Vector3.new(1e5,1e5,1e5)i.Parent=c;d=true;while d do local j=Vector3.new(h.right-h.left,h.up-h.down,h.forward-h.back)j=workspace.CurrentCamera.CFrame:VectorToWorldSpace(j).Unit;if j.Magnitude~=j.Magnitude then j=Vector3.new(0,0,0)end;i.Velocity=j*e;g.RenderStepped:Wait()end;i:Destroy()end;function onInputBegan(k,l)if l then return end;if k.KeyCode==Enum.KeyCode.E then d=not d;if d then fly()end elseif k.KeyCode==Enum.KeyCode.W then h.forward=1 elseif k.KeyCode==Enum.KeyCode.S then h.back=1 elseif k.KeyCode==Enum.KeyCode.A then h.left=1 elseif k.KeyCode==Enum.KeyCode.D then h.right=1 elseif k.KeyCode==Enum.KeyCode.Space then h.up=1 elseif k.KeyCode==Enum.KeyCode.LeftShift then h.down=1 end end;function onInputEnded(k)if k.KeyCode==Enum.KeyCode.W then h.forward=0 elseif k.KeyCode==Enum.KeyCode.S then h.back=0 elseif k.KeyCode==Enum.KeyCode.A then h.left=0 elseif k.KeyCode==Enum.KeyCode.D then h.right=0 elseif k.KeyCode==Enum.KeyCode.Space then h.up=0 elseif k.KeyCode==Enum.KeyCode.LeftShift then h.down=0 end end;f.InputBegan:Connect(onInputBegan)f.InputEnded:Connect(onInputEnded)print("Press E to toggle fly mode.")