local function a()for b,c in ipairs(workspace:GetDescendants())do if c.Name=="TheModelsNameYouWantToBeHighlighted"and c:IsA("Model")then if c:FindFirstChild("Highlight")then c.Highlight:Destroy()end;local d=Instance.new("Highlight")d.FillColor=Color3.new(1,1,1)d.OutlineColor=Color3.new(0,0,0)d.FillTransparency=0.8;d.OutlineTransparency=0.4;d.Parent=c end end end;while true do a()wait(1)end